using Banking.Net.Command.Accounts.Domain.Entities;using FluentNHibernate.Mapping;namespace Banking.Net.Command.Accounts.Infra.Persistence.NHibernate.Mapping{    public class BankAccountMap : ClassMap<BankAccount>    {        public BankAccountMap()        {            CompositeId(x => x.BankAccountId).KeyProperty(x => x.Id, y => y.ColumnName("bank_account_id"));            Component(x => x.BankAccountNumber, m =>            {                m.Map(x => x.Number, "number");            });            Component(x => x.Balance, m =>            {                m.Map(x => x.Amount, "balance");                m.Map(x => x.Currency, "currency");            });            Map(x => x.BankAccountStateId).CustomType<int>().Column("bank_account_state_id");            Map(x => x.CreatedAt).Column("created_at_utc");            Map(x => x.UpdatedAt).Column("updated_at_utc");            Component(x => x.CustomerId, m =>            {                m.Map(x => x.Id, "customer_id");            });        }    }}